import React, { Component } from 'react'; 
	var cardColor;

class Card extends Component {
	constructor (props) {
		super (props); 
		
		this.cardState = this.cardState.bind(this);
	}
	cardState () {
		var assign = 5 * Math.random();
		switch(cardColor = assign) {
			case 0:
			cardColor = 'red';
			break;
			case 1:
			cardColor = 'yellow'; 
			break;
			case 2: 
			cardColor = 'blue';
			break; 
			case 3: 
			cardColor = 'green'; 
			break; 
			case 4: 
			cardColor = 'gray'; 
			break; 
			case 5: 
			cardColor = 'white';
			break; 
			case 6: 
			cardColor = 'black';
			break; 
		}
	}
	render () {
		return (
			<td onClick={() => this.cardState()} style ={{backgroundColor: cardColor, border: 'solid 1px white' }}></td>
		);
	}
}


export default Card;
