import React from 'react'; 


function Card(props) {
	var colors = ['red', 'yellow', 'blue', 'green', 'gray','red', 'yellow', 'blue', 'green', 'gray']; 
	
	var cardColor; 

	switch(props) {
		case 0:
			cardColor = 'red' ;
    		break;
    	case 1:
    		cardColor = 'yellow'; 
    		break;
    	case 2: 
    		cardColor = 'blue';
    		break; 
    	case 3: 
    		cardColor = 'green'; 
    		break; 
    	case 4: 
    		cardColor = 'gray'; 
    		break; 
    	case 5: 
    		cardColor = 'white'
    		break; 

    	default: 6
    		cardColor = 'black';
	}

	// if (props == 1){
	// 	cardColor = colors[Math.floor(Math.random()*colors.length)];
	// }else if (props == 0) {
	// 	cardColor = 'black'; 
	// }else {
	// 	cardColor = 'white'; 
	// }


	return (

		<td style ={{backgroundColor: cardColor, border: 'solid 1px white' }}></td>

		);
}

export default Card;
