import React from 'react';
import Card from './Card';
import Timer from'./Timer';
import './App.css';
import { useState } from 'react';
import _ from 'lodash';

const possibleCards = 
[
  { color : 'red', flipped: false, matched:false},
  { color : 'red', flipped: false, matched:false},
  { color : 'yellow', flipped: false, matched:false },
  { color : 'yellow', flipped: false, matched:false },
  { color : 'blue', flipped: false, matched:false },
  { color : 'blue', flipped: false, matched:false },
  { color : 'green', flipped: false, matched:false },
  { color : 'green', flipped: false, matched:false },
  { color : 'orange', flipped: false, matched:false },
  { color : 'orange', flipped: false, matched:false }
]

function resetBoard()
{
  return _.cloneDeep(_.shuffle(possibleCards)); //clones the object so original remains in tact
}

function flipCard(cardId)
{
  let firstcard = false;
}

function App() {
  const[cards, setCards] = useState(resetBoard()); 
  const[victory, setVictory] = useState(false); //0 no victory, 1 victory
  const[timerOn, setTimerOn] = useState(false); //0 timer off, 1 timer on
  return (
    <div className="App">
      <Timer/> 
      <table>
        <tr>
          <Card card = {cards[0]} onClick={() => flipCard(0)}/> 
          <Card card = {cards[1]} onClick={() => flipCard(1)}/>
          <Card card = {cards[2]} onClick={() => flipCard(2)}/>
          <Card card = {cards[3]} onClick={() => flipCard(3)}/>
          <Card card = {cards[4]} onClick={() => flipCard(4)}/>
        </tr>
        <tr>
          <Card card = {cards[5]} onClick={() => flipCard(5)}/>
          <Card card = {cards[6]} onClick={() => flipCard(6)}/>
          <Card card = {cards[7]} onClick={() => flipCard(7)}/>
          <Card card = {cards[8]} onClick={() => flipCard(8)}/>
          <Card card = {cards[9]} onClick={() => flipCard(9)}/> 
        </tr>
      </table>
      <button onClick={() => setCards(resetBoard())}> Reset </button> 
    </div>
  );
}

export default App;
