import React, { Component } from 'react'; 

class Card extends Component {
	constructor (props) {
		super (props); // props include card state (0,1,2) and card id (1-5)
	}
	render () {
		let cardColor = 'black';
		if (this.props.cardState == 1) 
		{ // card is flipped and based on Id a color is assigned
			switch(this.state.cardColor = this.props.cardId) 
			{
				case 0:
					cardColor = 'red';
				break;
				case 1:
					cardColor = 'yellow';
				break;
				case 2: 
					cardColor = 'blue';
				break; 
				case 3: 
					cardColor = 'green';
				break; 
				case 4: 
					cardColor = 'orange';
				break; 
			}
		}
		else 
		{  // two cards have matched
//			cardColor = 'white';
		}
		return (
			<td style ={{backgroundColor: cardColor, border: 'solid 1px white' }}></td>
			);
	}
}


export default Card;
