import React from 'react';
import Card from './Card';
import Timer from'./Timer';
import './App.css';
import { useState } from 'react';
import _ from 'lodash';

const possibleCards = 
[
  { color : 'red', flipped: false},
  { color : 'red', flipped: false },
  { color : 'yellow', flipped: false },
  { color : 'yellow', flipped: false },
  { color : 'blue', flipped: false },
  { color : 'blue', flipped: false },
  { color : 'green', flipped: false },
  { color : 'green', flipped: false },
  { color : 'orange', flipped: false },
  { color : 'orange', flipped: false }
]

function resetBoard()
{
  return _.cloneDeep(_.shuffle(possibleCards)); //clones the object so original remains in tact
}

function App() {
  const[cards, setCards] = useState(resetBoard()); 
  const[victory, setVictory] = useState(false); //0 no victory, 1 victory
  const[timerOn, setTimerOn] = useState(false); //0 timer off, 1 timer on
  return (
    <div className="App">
      <Timer/> 
      <table>
        <tr>
          <Card card = {cards[0]}/> 
          <Card card = {cards[1]}/>
          <Card card = {cards[2]}/>
          <Card card = {cards[3]}/>
          <Card card = {cards[4]}/>
        </tr>
        <tr>
          <Card card = {cards[5]}/>
          <Card card = {cards[6]}/>
          <Card card = {cards[7]}/>
          <Card card = {cards[8]}/>
          <Card card = {cards[9]}/> 
        </tr>
      </table>
      <button onClick={() => resetBoard()}> Reset </button> 
    </div>
  );
}

export default App;
