import React, { Component } from 'react'; 


class Card extends Component {
	constructor (props) {
		super (props); 

		this.state = {
			cardColor : "black"
		}
		
		this.cardState = this.cardState.bind(this);
	}
	cardState () {
		var assign = Math.floor(Math.random()*this.props.cardState.cardId);
		console.log(this.props.cardState.cardId);
		switch(this.state.cardColor = assign) {
			case 0:
			this.setState({cardColor: 'red'});
			break;
			case 1:
			this.setState({cardColor: 'yellow'});
			break;
			case 2: 
			this.setState({cardColor: 'blue'});
			break; 
			case 3: 
			this.setState({cardColor: 'green'});
			break; 
			case 4: 
			this.setState({cardColor: 'gray'});
			break; 
		}
		
	}
	render () {
		return (
			<td onClick={() => this.cardState()} style ={{backgroundColor: this.state.cardColor, border: 'solid 1px white' }}></td>
		);
	}
}


export default Card;
