import React from 'react';
import Card from './Card';
import Timer from'./Timer';
import './App.css';
import { useState } from 'react';
import _ from 'lodash';

const possibleCards = 
[
  { color : 'red', flipped: false, matched:false},
  { color : 'red', flipped: false, matched:false},
  { color : 'yellow', flipped: false, matched:false },
  { color : 'yellow', flipped: false, matched:false },
  { color : 'blue', flipped: false, matched:false },
  { color : 'blue', flipped: false, matched:false },
  { color : 'green', flipped: false, matched:false },
  { color : 'green', flipped: false, matched:false },
  { color : 'orange', flipped: false, matched:false },
  { color : 'orange', flipped: false, matched:false }
]

let previousCard = 
{
  previousColor: 'black',
  previousIndex: -1
}

function resetBoard()
{
  return _.cloneDeep(_.shuffle(possibleCards)); //clones the object so original remains in tact
}

function flipCard(cardId, cards, setCards)
{
  var i;
  for(i = possibleCards.length ; i > 0; i--)
  {
    if(!cards[i].flipped) 
    {
      cards[cardId].flipped = true;
      previousCard.previousColor = cards[cardId].color;
      previousCard.previousIndex = cardId;
    }
    else
    {
      cards[cardId].flipped = true;
      if(cards[cardId].color === previousCard)
      {
        cards[cardId].matched = true;
        previousCard = 'black';
      }
      else
      {
        let interval;
        interval = setTimeout(() => {
          cards[cardId].flipped = false;
          cards[previousCard.previousIndex].flipped = false;
        }, 1000);
      }
    }
  }
}

function App() {
  const[cards, setCards] = useState(resetBoard()); 
  const[victory, setVictory] = useState(false); //0 no victory, 1 victory
  const[timerOn, setTimerOn] = useState(false); //0 timer off, 1 timer on
  return (
    <div className="App">
      <Timer/> 
      <table>
        <tr>
          <Card card = {cards[0]} onClick={() => flipCard(0, cards, setCards)}/> 
          <Card card = {cards[1]} onClick={() => flipCard(1, cards, setCards)}/>
          <Card card = {cards[2]} onClick={() => flipCard(2, cards, setCards)}/>
          <Card card = {cards[3]} onClick={() => flipCard(3, cards, setCards)}/>
          <Card card = {cards[4]} onClick={() => flipCard(4, cards, setCards)}/>
        </tr>
        <tr>
          <Card card = {cards[5]} onClick={() => flipCard(5, cards, setCards)}/>
          <Card card = {cards[6]} onClick={() => flipCard(6, cards, setCards)}/>
          <Card card = {cards[7]} onClick={() => flipCard(7, cards, setCards)}/>
          <Card card = {cards[8]} onClick={() => flipCard(8, cards, setCards)}/>
          <Card card = {cards[9]} onClick={() => flipCard(9, cards, setCards)}/> 
        </tr>
      </table>
      <button onClick={() => setCards(resetBoard())}> Reset </button> 
    </div>
  );
}

export default App;
